import React, { Component } from 'react'
import {Buttons, Controller, Dashboard} from "./Buttons.styles.jsx"


class ControlButtons extends Component { 

    constructor(){
        super()
        this.state = {
            Temperature: null,
            Height: null,
            Angle: null,
            RotationSpeed: null
        }
    }

    // const {Temperature } = this.state

    render (){
    return( 
            <Buttons>
                <Controller>
                <section class="buttons">
                <h1>Dji Tello Controller</h1>

                    <buttons>UP</buttons>
                    <buttons>RIGHT</buttons>
                    <buttons>DOWN</buttons>
                    <buttons>LEFT</buttons>
                </section>
                    </Controller>
<Dashboard>
                <section class="T">

                    <h1>Digi Dashboard</h1>
                        <h3>Temperature:  84 {this.state.temperature} degrees</h3>
                        <h3>Height : 432 {this.state.Height} Ft</h3>
                        <h3>Angle: 90 {this.state.Angle}</h3>                        
                        <h3>Rotation Speed: 783 {this.state.RotationSpeed} degrees</h3>

</section>

                    </Dashboard>
            </Buttons>
    )
    }
}

export default ControlButtons;
